const defaultState = {
    storage: [],
    living: [],
    bedroom: [],
    dining: [],
    other: [],
    choices: {
        'storage': true,
        'living': false,
        'bedroom': false,
        'dining': false,
        'other': false,
    }
}
export default(state = defaultState, action) =>{
    if(action.type === 'initDataAction'){
        const newState = JSON.parse(JSON.stringify(state));
        newState.bedroom = action.data.bedroom;
        newState.dining = action.data.living;
        newState.other = action.data.other;
        newState.storage = action.data.storage;
        console.log(newState)
        return newState;
    }
    if(action.type === 'changeChoice'){
        const newState = JSON.parse(JSON.stringify(state));
        const choice= {
            'storage': false,
            'living': false,
            'bedroom': false,
            'dining': false,
            'other': false,
        };
        choice[action.value] = true
        return newState;
    }
    return state;
}
