import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
import Collapse_Fragment from './Collapse_Fragment'
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
class Home_Collapse extends Component{
    _isMounted = false;
    constructor(props){
        super(props);
        this.state = store.getState();
        this.handleStoreChange = this.handleStoreChange.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    render(){
        const style = {
            collapsed: {
                display: "none"
            },
            expanded: {
                display: "block"
            }
        }
        const arr = ['storage', 'bedroom', 'living', 'other', 'dining'];
        return (
            <Fragment>
                {arr.map(key=>{
                    return (
                        <div key={key}>
                            <div>
                                <span>{key}</span>
                                <button onClick={this.handleClickChange.bind(this, key)}>点击</button>
                            </div>
                            <div style={this.state.choices[key]?style.expanded:style.collapsed}>
                                <Collapse_Fragment type_name={key} />
                            </div>
                        </div>
                    )
                })}
            </Fragment>
        )
    }

    handleStoreChange(){
        this.setState(store.getState());
    }
    handleClickChange(key){
        const cho = {
            storage : false,
            bedroom: false,
            living: false,
            other: false,
            dining: false
        }
        cho[key] = true;
       this.setState({
           choices: cho
       })
    }

    componentDidMount(){

        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home_Collapse;
