import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
import './home.css';
import Types from './Types';
class Home extends Component{
    constructor(props){
        super(props);
        this.state= {
            storage: [],
            living: [],
            bedroom: [],
            dining: [],
                other: [],
            choices: {
                'storage': true,
                'living': false,
                'bedroom': false,
                'dining': false,
                'other': false,
            }
        };
        this.handleStoreChange = this.handleStoreChange.bind(this);
        this.changeChoice = this.changeChoice.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    render(){
        return (
            <Fragment>
                {Object.keys(this.state.data).map(key=>{
                    return this.state.choices[key]?(
                    <div key={key}>
                        <div><button onClick={this.changeChoice(key)}>点击</button></div>
                        <div><Types type_name={key} id={key}></Types></div>
                    </div>):(<div key={key}><button onClick={this.changeChoice(key)}>点击</button></div>)
                })}
            </Fragment>
        );
    }

    changeChoice(key){
        this.setState()
    }

    handleStoreChange(){
        this.setState({data:store.getState()});
    }

    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home;
