const defaultState = {
    data: {
        storage : {},
        bedroom : {},
        living : {},
        other : {},
        dining : {},
    },
    choices : {
        storage : true,
        bedroom: false,
        living: false,
        other: false,
        dining: false
    }
}
export default(state = defaultState, action) =>{
    if(action.type === 'initDataAction'){
        const newState = JSON.parse(JSON.stringify(state));
        for(let key in newState.data){
            action.data[key].forEach(element => {
                newState.data[key][element._uid] = {
                    date: element.date,
                    image: element.image,
                    content: element.content,
                    ig_handle: element.ig_handle
                }
            });
        }
        return newState;
    }
    return state;
}
