import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
import './home.css';
import { act } from '@testing-library/react';
class Home extends Component{
    constructor(props){
        super(props);
        this.state = store.getState();
        this.handleStoreChange = this.handleStoreChange.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    render(){
        const style = {
            collapsed: {
              display: "none"
            },
            expanded: {
              display: "block"
            }
          };
        return (
            <Fragment>
                {Object.keys(this.state.choices).map(key=>{
                    return (
                    <div key={key} id=''>
                            <div>
                                <span>{key}</span>
                                <button onClick={this.changeChoices.bind(this,key)}>点击</button>
                            </div>
                            <div style={this.state.choices[key]?style.display:style.collapsed}>
                                {this.state[key].map((value)=>{
                                        return (
                                            <div key={value._uid} id="produces">
                                                <img id="imgs" src={value.image}/>
                                                <div id="simple_info">
                                                    <span id="produces_name">{value.ig_handle}</span>
                                                    <span id="produces_date">{value.date}</span>
                                                </div>
                                            </div>
                                        )
                                    })
                                }
                            </div>
                    </div>
                    )
                })}
            </Fragment>
        );
    }

    handleStoreChange(){
        this.setState(store.getState());
    }

    changeChoices(key){
        const action = {
            type: 'changeChoice',
            data:key
        }
        store.dispatch(action)
    }

    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home;
