import React, {Component} from 'react';
import 'antd/dist/antd.css';
import store from './store';
class AddItem extends Component{
    constructor(props){
        super(props)
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state={
            date:'',
            type:'',
            ig_handle:'',
            image:'',
            content:''
        }
    }
    render(){
        return (
            <div>
            <h3>Add Item</h3>
            <form onSubmit={this.handleSubmit}>
                <table>
                    <thead></thead>
                    <tbody>
                        <tr><td>ig_handle</td><td><input onChange={this.handleListener.bind(this, "ig_handle")} value={this.state.ig_handle} type='text' required/></td></tr>
                        <tr><td>image</td><td><input onChange={this.handleListener.bind(this, "image")} value={this.state.image} type="url" required/></td></tr>
                        <tr><td>content</td><td><textarea onChange={this.handleListener.bind(this, "content")} value={this.state.content} required></textarea></td></tr>
                        <tr>
                            <td>type</td>
                            <td>
                                <select value={this.state.type} onChange={this.handleListener.bind(this, "type")}>
                                    <option value=''>*</option>
                                    <option value='storage'>storage</option>
                                    <option value='living'>living</option>
                                    <option value='dining'>dining</option>
                                    <option value='bedroom'>bedroom</option>
                                    <option value='other'>other</option>
                                </select>
                            </td>
                        </tr>
                        <tr><td>date</td><td><input onChange={this.handleListener.bind(this, "date")} value={this.state.date} type='date' required/></td></tr>
                    </tbody>
                </table>
            <input type="submit" value="Submit" />
            </form>
        </div>
        )
    }
    handleSubmit(event) {
        event.preventDefault();
        const action = {
            type:"addItem",
            value: this.state
        }
        store.dispatch(action)
    }
    handleListener(type, e){
        const value = e.target.value;
        this.setState({
            [type]: value
        })
    }
}
export default AddItem;
