import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
class Home extends Component{
    constructor(props){
        super(props);
        this.state={
            storage_show: true,
        }
        this.showContent = this.showContent.bind(this);
    }
    render(){
        const style = {
            collapsed:{
                display: "none"
            },
            expanded:{
                display: "block"
            }
        }
        return (
            <Fragment>
                <button onClick={this.showContent}>click</button>
                <div style={this.state.show? style.expanded: style.collapsed}>123</div>
                <div></div>
                <button onClick={this.showContent}>click</button>
                <div style={!this.state.show? style.expanded: style.collapsed}>123</div>
            </Fragment>
        )
    }
    showContent(){
        const show = this.state.show;
        this.setState({
            show: !show
        })
    }
    componentDidMount () {
        this.getList()
      }

      async getList() {
        const that = this
        const params = {
          pageNo: 1,
          pageSize: 5
        }
        axios({
          method: "GET",
          url: "/api/article/list",
          params
        })
        .then(function (response) {

          if (response.data.code === 1000) {
            // console.log(response.data.data);
            that.setState({
              data: response.data.data
            })
          }
        })
        .catch(function (error) {
          console.log(error);
        });
      }
}
export default Home;
