import React, {Component, Fragment} from 'react';
import './type.css';
import store from './store';
class Types extends Component{
    constructor(props){
        super(props);
        this.state = {
            type: []
        }
        this.handleStoreChange = this.handleStoreChange.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    componentDidUpdate(){
        const action = {
            type: 'search_by_typename',
            value: this.props.type_name
        }
        store.dispatch(action)
    }
    render(){
        console.log(this.state.type[this.props.type_name])
        return(
            <Fragment>
                <div id="produces_type">
                    {this.state.type[this.props.type_name].map((value)=>{
                        return (
                            <div key={value._uid} id="produces">
                                <img src={value.image} id="imgs"/>
                                <div id="simple_info">
                                    <span id="produces_name">{value.ig_handle}</span>
                                    <span id="produces_date">{value.date}</span>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </Fragment>
        )
    }
    handleStoreChange(){
        this.setState(store.getState());
    }
}
export default Types;
