import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
import './home.css';
import Types from './Types';
class Home extends Component{
    constructor(props){
        super(props);
        this.state= null;
        this.handleStoreChange = this.handleStoreChange.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    render(){
        return (
            <Fragment>
                {
                    this.state !== null? (<Types type_name={'storage'} key={'storage'}></Types>): <div></div>,
                    this.state !== null? (<Types type_name={'bedroom'} key={'bedroom'}></Types>): <div></div>,
                    this.state !== null? (<Types type_name={'living'} key={'living'}></Types>): <div></div>,
                    this.state !== null? (<Types type_name={'dining'} key={'dining'}></Types>): <div></div>,
                    this.state !== null? (<Types type_name={'other'} key={'other'}></Types>): <div></div>
                }
            </Fragment>
        );
    }
    handleStoreChange(){
        this.setState(store.getState());
    }
    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home;
