const defaultState = {
    storage: [],
    living: [],
    bedroom: [],
    dining: [],
    other: []
}
export default(state = defaultState, action) =>{
    if(action.type === 'initDataAction'){
        const newState = {};
        newState.bedroom = action.data.bedroom;
        newState.dining = action.data.living;
        newState.other = action.data.other;
        newState.storage = action.data.storage;
        return newState;
    }
    if(action.type === 'search_by_typename'){
        const newState = {}
        newState.type = state[action.value];
        return newState;
    }
    return state;
}
