import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
import './home.css';
import Types from './Types';
class Home extends Component{
    constructor(props){
        super(props);
        this.state= {
            storage: [],
            living: [],
            bedroom: [],
            dining: [],
            other: [],
            choices: {
                'storage': true,
                'living': false,
                'bedroom': false,
                'dining': false,
                'other': false,
            }
        };
        this.handleStoreChange = this.handleStoreChange.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    render(){
        console.log(this.state)
        return (
            <Fragment>
                {Object.keys(this.state.choices).map(key=>{
                    return (
                    <div key={key}>
                        <div>
                            <div>
                                <span>{this.state.type_name}</span>
                                <button>点击</button>
                            </div>
                            {this.state[this.state.type_name]?this.state[this.state.type_name].map((value)=>{
                                    return (
                                        <div key={value._uid} id="produces">
                                            <div id="simple_info">
                                                <span id="produces_name">{value.ig_handle}</span>
                                                <span id="produces_date">{value.date}</span>
                                            </div>
                                        </div>
                                    )
                                }):<div></div>
                            }
                        </div>
                    </div>)
                })}
            </Fragment>
        );
    }

    handleStoreChange(){
        this.setState({data:store.getState()});
    }

    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home;
