const defaultState = {
    badroom
}
export default(state = defaultState, action) =>{
    if(action.type === 'initDataAction'){
        const newState = JSON.parse(JSON.stringify(state));
        newState.bedroom = {}
        newState.living = action.data.living;
        newState.other = action.data.other;
        newState.storage = action.data.storage;
        newState.dining = action.data.dining;
        return newState;
    }
    return state;
}
