import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
class Home extends Component{
    constructor(props){
        super(props);
        this.state={
            storage_show: true,
        }
        this.showContent = this.showContent.bind(this);
    }
    render(){
        const style = {
            collapsed:{
                display: "none"
            },
            expanded:{
                display: "block"
            }
        }
        return (
            <Fragment>
                <button onClick={this.showContent}>click</button>
                <div style={this.state.show? style.expanded: style.collapsed}>123</div>
                <div></div>
                <button onClick={this.showContent}>click</button>
                <div style={!this.state.show? style.expanded: style.collapsed}>123</div>
            </Fragment>
        )
    }
    showContent(){
        const show = this.state.show;
        this.setState({
            show: !show
        })
    }
    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispac
        })
    }
}
export default Home;
