const defaultState = {
    bedroom : {},
    living : {},
    other : {},
    storage : {},
    dining : {}
}
export default(state = defaultState, action) =>{
    if(action.type === 'initDataAction'){
        const newState = JSON.parse(JSON.stringify(state));
        for(let key in newState){
            console.log(key) 
        }
        action.data.bedroom.forEach(element => {
            newState.bedroom[element._uid] = {
                date: element.date,
                image: element.image,
                content: element.content,
                ig_handle: element.ig_handle
            }
        });
        newState.living = action.data.living;
        newState.other = action.data.other;
        newState.storage = action.data.storage;
        newState.dining = action.data.dining;
        return newState;
    }
    return state;
}
