import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
import './home.css';
class Home extends Component{
    constructor(props){
        super(props);
        this.state= {
            bedroom: [],
            dining: [],
            living: [],
            new_way_of_living: [],
            other: [],
            storage: []
        }
        this.handleStoreChange = this.handleStoreChange.bind(this);
        store.subscribe(this.handleStoreChange);
    }
    render(){
        console.log(this.state);
        return (
            <Fragment>
                <div id="produces_type">
                    {this.state.bedroom.map((value)=>{
                        return (
                            <div key={value._uid} id="produces">
                                <img src={value.image} id="imgs"/>
                                <div>
                                    <p>{value.ig_handle}</p>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </Fragment>
        )
    }
    handleStoreChange(){
        this.setState(store.getState());
    }
    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home;
