import store from './store';
import React, {Component, Fragment} from 'react';
import axios from 'axios';
class Home extends Component{
    constructor(props){
        super(props);
        this.handleStoreChange = this.handleStoreChange.bind
    }
    render(){
        return (
            <Fragment>

            </Fragment>
        )
    }
    componentDidMount(){
        axios.defaults.baseURL = '/api';
        axios.get("").then((res)=>{
            const data = res.data.story.content;
            const action = {
                type: 'initDataAction',
                data: data
            }
            store.dispatch(action)
        })
    }
}
export default Home;
